### 实践：通过vue-cli配置代理进行跨域。
1. 在项目根目录添加vue.config.js文件（如果已经有则跳过）。
2. 添加如下代码：
```javascript
module.exports = {
  devServer: {  // 该配置仅针对开发模式有效
    proxy: {    // 该对象下的内容为代理配置
      '/api': { // 代理具有'\api'前缀的请求
        target: 'http://localhost:8080/video_war/', // 将匹配的前缀（本示例中为'api'）改为target属性的内容。
        changeOrigin: true, // 
        pathRewrite: {
          '^/api': ''
        }
      }
    }
  }
}
```

/**
 * 
 * @param {*} parentElm 父元素 
 * @param {*} oldCh 旧的子节点
 * @param {*} newCh 新的子节点
 */
function updateChildren(parentElm, oldCh, newCh) {
    let oldStartIdx = 0;
    let newStartIdx = 0;
    let oldEndIdx = oldCh.length - 1;
    let oldStartVnode = oldCh[0];
    let oldEndVnode = oldCh[oldEndIdx];
    let newEndIdx = newCh.length - 1;
    let newStartVnode = newCh[0];
    let newEndVnode = newCh[newEndIdx];
    let oldKeyToIdx, idxInOld, elmToMove, refElm;

    while (oldStartIdx <= oldEndIdx && newStartIdx <= newEndIdx) {
        if (!oldStartVnode) {
            // 如果旧的start vnode不存在，则获取旧的start vnode。
            oldStartVnode = oldCh[++oldStartIdx];
        } else if (!oldEndVnode) {
            // 如果旧的end vnode不存在，则获取旧的end vnode
            oldEndVnode = oldCh[--oldEndIdx];
        } else if (sameVnode(oldStartVnode, newStartVnode)) {
            // 如果旧的开始节点和新的开始节点一致，比对这两个节点的属性等信息。
            // 然后再获取新的old start vnode和新的new start vnode
            patchVnode(oldStartVnode, newStartVnode);
            oldStartVnode = oldCh[++oldStartIdx];
            newStartVnode = newCh[++newStartIdx];
        } else if (sameVnode(oldEndVnode, newEndVnode)) {
            // 如果旧的end vnode和新的end vnode相同，则比较
            patchVnode(oldEndVnode, newEndVnode);
            oldEndVnode = oldCh[--oldEndIdx];
            newEndVnode = newCh[--newEndIdx];
        } else if (sameVnode(oldStartVnode, newEndVnode)) {
            patchVnode(oldStartVnode, newEndVnode);
            nodeOps.insertBefore(parentElm, oldStartVnode.elm, nodeOps.nextSibling(oldEndVnode.elm));
            oldStartVnode = oldCh[++oldStartIdx];
            newEndVnode = newCh[--newEndIdx];
        } else if (sameVnode(oldEndVnode, newStartVnode)) {
            patchVnode(oldEndVnode, newStartVnode);
            nodeOps.insertBefore(parentElm, oldEndVnode.elm, oldStartVnode.elm);
            oldEndVnode = oldCh[--oldEndIdx];
            newStartVnode = newCh[++newStartIdx];
        } else {
            let elmToMove = oldCh[idxInOld];
            if (!oldKeyToIdx) oldKeyToIdx = createKeyToOldIdx(oldCh, oldStartIdx, oldEndIdx);
            idxInOld = newStartVnode.key ? oldKeyToIdx[newStartVnode.key] : null;
            if (!idxInOld) {
                createElm(newStartVnode, parentElm);
                newStartVnode = newCh[++newStartIdx];
            } else {
                elmToMove = oldCh[idxInOld];
                if (sameVnode(elmToMove, newStartVnode)) {
                    patchVnode(elmToMove, newStartVnode);
                    oldCh[idxInOld] = undefined;
                    nodeOps.insertBefore(parentElm, newStartVnode.elm, oldStartVnode.elm);
                    newStartVnode = newCh[++newStartIdx];
                } else {
                    createElm(newStartVnode, parentElm);
                    newStartVnode = newCh[++newStartIdx];
                }
            }
        }
    }

    if (oldStartIdx > oldEndIdx) {
        refElm = (newCh[newEndIdx + 1]) ? newCh[newEndIdx + 1].elm : null;
        addVnodes(parentElm, refElm, newCh, newStartIdx, newEndIdx);
    } else if (newStartIdx > newEndIdx) {
        removeVnodes(parentElm, oldCh, oldStartIdx, oldEndIdx);
    }
}

function patch(oldVnode, vnode, parentElm) {
    if (!oldVnode) {
        // 老vnode不存在，将新的vnode直接插入元素。
        addVnodes(parentElm, null, vnode, 0, vnode.length - 1);
    } else if (!vnode) {
        // 新vnode不存在，直接删除原树上的元素。
        removeVnodes(parentElm, oldVnode, 0, oldVnode.length - 1);
    } else {
        // 两个vnode的key、标签相同，同时为注释节点、data同时定义或不定义、如果标签类型同时为Input，则type相同。
        // 满足以上所有条件则为vnode。
        if (sameVnode(oldVNode, vnode)) {
            // 对两个vnode进行patch。
            patchVnode(oldVNode, vnode);
        } else {
            // 如果不相同，将旧节点移除，插入新节点。
            removeVnodes(parentElm, oldVnode, 0, oldVnode.length - 1);
            addVnodes(parentElm, null, vnode, 0, vnode.length - 1);
        }
    }
}


let has = {};   // 利用has辅助判断watcher是否存在。
let queue = [];
let waiting = false;

function queueWatcher(watcher) {
    const id = watcher.id;
    if (has[id] == null) {
        has[id] = true;     // 添加watcher时，将相应的map设置为true。
        queue.push(watcher);

        if (!waiting) {
            waiting = true;
            nextTick(flushSchedulerQueue);
        }
    }
}

